
pub fn solve_part_1() {
    let mut floor = 0;
    for c in input().chars() {
        match c {
            '(' => floor += 1,
            ')' => floor -= 1,
            _ => panic!(),
        }
    }
    println!("Part 1: {}", floor);
}

pub fn solve_part_2() {
    let mut floor = 0;
    let mut index = 1;
    for c in input().chars() {
        match c {
            '(' => floor += 1,
            ')' => floor -= 1,
            _ => panic!(),
        }
        if floor == -1 {
            break;
        }
        index += 1;
    }
    println!("Part 2: {}", index);
}

